import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import { BiPhone } from "react-icons/bi";
import { RiLockPasswordLine } from "react-icons/ri";
import { IoMdLogIn } from "react-icons/io";
import { BiUserPlus } from "react-icons/bi";
import { Error } from "./ErrorMessage";
import "./login.css";
import { AiOutlineClose } from "react-icons/ai";
import swal  from "sweetalert";

import { NavLink, useHistory } from "react-router-dom";
import {} from "react-router";
import AOS from "aos";

import "aos/dist/aos.css";



export function Login() {
  const initialValue = {
    phone: "",
    password: "",
  };

  const phoneRegExp =
    /^((\+92)|(0092))-{0,1}\d{3}-{0,1}\d{7}$|^\d{11}$|^\d{4}-\d{7}$/;

  const validationSchema = Yup.object({
    phone: Yup.string()
      .required("Phone Number Missing")
      .matches(phoneRegExp, "Phone number is not valid"),
    password: Yup.string().required(" Password Required").min(6),
  });
  const onSubmit = async (values) => {
    const res = await fetch("http://localhost:5000/api/v1/user/signin", {
  method: "POST",
  headers: {
    "Content-Type": "application/json",
  },
  body: JSON.stringify({
    phone: `${values.phone}`,
    password: `${values.password}`,

  }),
});
const data = await res.json();
console.log(data.message);

if (data.status === 500 || !data || data.success === false) {
  swal("Error!", data.message, "error");
  console.log(data);
} else {
  console.log(data);
  swal("Success!", data.message, "success");
  history.push("/login")
}
};
  let history = useHistory();
  const goPrevious = () => {
    history.goBack();
  };
  React.useEffect(() => {
    AOS.init({
      duration: 500,
    });
  }, []);
  return (
    <>

        <div className="background">
          <div className="form-login" data-aos="zoom-in">
            <AiOutlineClose
              onClick={goPrevious}
              className="login-cross"
              size="30"
            />

            <div>
              <h1>login</h1>
            </div>

            <Formik
              validationSchema={validationSchema}
              onSubmit={onSubmit}
              initialValues={initialValue}
            >
              <Form method="POST">
                <div className="phone">
                  <div className="phone-inner">
                    <BiPhone className="login-logo" size="35" />
                    <Field type="text" id="phone" name="phone" />
                  </div>
                  <ErrorMessage component={Error} name="phone" />
                </div>
                <div className="password">
                  <div className="phone-inner">
                    <RiLockPasswordLine size="35" className="login-logo" />
                    <Field type="password" id="password" name="password" />
                  </div>
                  <ErrorMessage component={Error} name="password" />
                </div>
                <div className="login-button">
                  <IoMdLogIn size="35" className="login-logo" />
                  <button type="submit">
                    Login
                  </button>
                </div>
              </Form>
            </Formik>
            <NavLink to="/signup">
            <div className="login-button">
              <BiUserPlus size="35" className="login-logo" />
              <button  type="submit">
                 Sign Up
              </button>
            </div>
            </NavLink>
          </div>
        </div>

    </>
  );
}
